extension.install:
// Setup main table
if (!$forum_db->table_exists('hcs_ads_code'))
{
	$schema = array(
		'FIELDS'			=> array(
			'id'				=> array(
				'datatype'		=> 'SERIAL',
				'allow_null'	=> false
			),
			'name'			=> array(
				'datatype'		=> 'VARCHAR(255)',
				'allow_null'	=> false,
				'default'		=> '\'\''
			),
			'enabled'			=> array(
				'datatype'		=> 'TINYINT(1)',
				'allow_null'	=> false,
				'default'		=> '\'0\''
			),
			'code'			=> array(
				'datatype'		=> 'TEXT',
				'allow_null'	=> false,
				'default'		=> '\'\''
			),
			'page_index'			=> array(
				'datatype'		=> 'VARCHAR(255)',
				'allow_null'	=> false,
				'default'		=> '\'\''
			),
			'page_forum'			=> array(
				'datatype'		=> 'VARCHAR(255)',
				'allow_null'	=> false,
				'default'		=> '\'\''
			),
			'page_topic'			=> array(
				'datatype'		=> 'VARCHAR(255)',
				'allow_null'	=> false,
				'default'		=> '\'\''
			),
			'page_search'			=> array(
				'datatype'		=> 'VARCHAR(255)',
				'allow_null'	=> false,
				'default'		=> '\'\''
			),

		),
		'PRIMARY KEY'	=> array('id'),
		'INDEXES'		=> array(
			'name_idx'		=> array('name' )
		)
	);

	$forum_db->create_table('hcs_ads_code', $schema);

}

if (!$forum_db->field_exists('file_name')){
	$forum_db->add_field('hcs_ads_code', 'file_name', 'varchar(255)', false, '');
}

hooks.hook:
if (!empty($hcs_ads_placeholder[0])) {
	$tpl_main = str_replace('<!-- hcs_ads_top_placeholder -->', implode(' ',$hcs_ads_placeholder[0]), $tpl_main);
}
if (!empty($hcs_ads_placeholder[1])) {
	$tpl_main = str_replace('<!-- hcs_ads_bottom_placeholder -->', implode(' ',$hcs_ads_placeholder[1]), $tpl_main);
}
